package com.example.oportunyfam.Service

import com.example.oportunyfam.model.AtividadeRequest
import com.example.oportunyfam.model.AtividadeResponse
import com.example.oportunyfam.model.AulaRequest
import com.example.oportunyfam.model.AulaResponse
import retrofit2.Call
import retrofit2.http.*

interface AtividadeService {

    // --- ROTAS DE ATIVIDADE (CRUD) ---

    // POST /v1/atividades/
    @Headers("Content-Type: application/json")
    @POST("atividades")
    fun criarAtividade(@Body atividade: AtividadeRequest): Call<AtividadeResponse>

    // PUT /v1/atividades/:id
    @Headers("Content-Type: application/json")
    @PUT("atividades/{id}")
    fun atualizarAtividade(@Path("id") id: Int, @Body atividade: AtividadeRequest): Call<AtividadeResponse>

    // DELETE /v1/atividades/:id
    @DELETE("atividades/{id}")
    fun deletarAtividade(@Path("id") id: Int): Call<Unit>

    // GET /v1/atividades/ (Busca todas, possivelmente com filtros)
    @GET("atividades")
    fun buscarTodasAtividades(): Call<List<AtividadeResponse>> // Retorna detalhes da View

    // GET /v1/atividades/:id (Busca por ID)
    @GET("atividades/{id}")
    fun buscarAtividadePorId(@Path("id") id: Int): Call<AtividadeResponse>

    // --- ROTAS DE AULAS (CRUD) ---
    // Nota: O prefixo "/aulas" é adicionado à URL base do Retrofit, se a rota for absoluta.
    // Se a rota for relativa (como aqui), ela é concatenada com o prefixo do controller.

    // POST /v1/atividades/aulas/
    @POST("atividades/aulas")
    fun criarAula(@Body aula: AulaRequest): Call<AulaResponse>

    // PUT /v1/atividades/aulas/:id
    @PUT("atividades/aulas/{id}")
    fun atualizarAula(@Path("id") id: Int, @Body aula: AulaRequest): Call<AulaResponse>

    // DELETE /v1/atividades/aulas/:id
    @DELETE("atividades/aulas/{id}")
    fun deletarAula(@Path("id") id: Int): Call<Unit>

    // GET /v1/atividades/aulas/:id (Buscar aula específica)
    @GET("atividades/aulas/{id}")
    fun buscarAulaPorId(@Path("id") id: Int): Call<AulaResponse> // Usamos AulaResponse aqui, não o HorarioDetalhe

    // GET /v1/atividades/aulas (Buscar todas as aulas)
    @GET("atividades/aulas")
    fun buscarTodasAulas(): Call<List<AulaResponse>>

    // GET /v1/atividades/aulas/instituicao/:idInstituicao (Buscar aulas por Instituição)
    @GET("atividades/aulas/instituicao/{idInstituicao}")
    fun buscarAulasPorInstituicao(@Path("idInstituicao") idInstituicao: Int): Call<List<AulaResponse>>
}

package com.example.oportunyfam.Service

import com.example.oportunyfam.model.*
import retrofit2.Call
import retrofit2.http.*

interface AtividadeService {

    // --- ROTAS DE ATIVIDADE ---

    // POST /atividades/ - Criar atividade
    @Headers("Content-Type: application/json")
    @POST("atividades")
    fun criarAtividade(@Body atividade: AtividadeRequest): Call<AtividadeCriadaResponse>

    // GET /atividades/ - Buscar todas as atividades
    @GET("atividades")
    fun buscarTodasAtividades(): Call<AtividadesListResponse>

    // GET /atividades/:id - Buscar atividade por ID
    @GET("atividades/{id}")
    fun buscarAtividadePorId(@Path("id") id: Int): Call<AtividadeUnicaResponse>

    // GET /atividades/instituicao/:idInstituicao - Buscar atividades por instituição
    @GET("atividades/instituicao/{idInstituicao}")
    fun buscarAtividadesPorInstituicao(@Path("idInstituicao") idInstituicao: Int): Call<AtividadesListResponse>

    // PUT /atividades/:id - Atualizar atividade
    @Headers("Content-Type: application/json")
    @PUT("atividades/{id}")
    fun atualizarAtividade(@Path("id") id: Int, @Body atividade: AtividadeRequest): Call<AtividadeCriadaResponse>

    // DELETE /atividades/:id - Deletar atividade
    @DELETE("atividades/{id}")
    fun deletarAtividade(@Path("id") id: Int): Call<Unit>

    // --- ROTAS DE AULAS ---

    // POST /aulas/ - Criar uma única aula
    @Headers("Content-Type: application/json")
    @POST("aulas")
    fun criarAula(@Body aula: AulaRequest): Call<AulaCriadaResponse>

    // POST /aulas/lote - Criar várias aulas de uma vez
    @Headers("Content-Type: application/json")
    @POST("aulas/lote")
    fun criarAulasLote(@Body aulaLote: AulaLoteRequest): Call<AulaLoteResponse>

    // GET /aulas/:id - Buscar aula por ID
    @GET("aulas/{id}")
    fun buscarAulaPorId(@Path("id") id: Int): Call<AulaCriadaResponse>

    // PUT /aulas/:id - Atualizar aula
    @Headers("Content-Type: application/json")
    @PUT("aulas/{id}")
    fun atualizarAula(@Path("id") id: Int, @Body aula: AulaRequest): Call<AulaCriadaResponse>

    // DELETE /aulas/:id - Deletar aula
    @DELETE("aulas/{id}")
    fun deletarAula(@Path("id") id: Int): Call<Unit>
}

package com.example.oportunyfam.model
data class AtividadeResponse( val atividade_id: Int, // Mapeia para 'atividade_id' da View val titulo: String, val descricao: String? = null, val faixa_etaria_min: Int, val faixa_etaria_max: Int, val gratuita: Boolean, val preco: Double, val ativo: Boolean,
// CAMPOS JOIN DA VIEW val categoria: String, // c.nome AS categoria val instituicao_id: Int, val instituicao: String, // i.nome AS instituicao val cidade: String, val estado: String,
// ARRAY DE HORÁRIOS DA VIEW val horarios: List<HorarioDetalhe> // Lista da classe definida acima (AulaResponse simplificada) )

package com.example.oportunyfam.model
import com.google.gson.annotations.SerializedName
// Response para atividade simples (após cadastro) data class AtividadeCriadaResponse( val status: Boolean, val status_code: Int, val messagem: String, val atividade: AtividadeSimples )
data class AtividadeSimples( val id: Int, val id_instituicao: Int, val id_categoria: Int, val titulo: String, val descricao: String, val faixa_etaria_min: Int, val faixa_etaria_max: Int, val gratuita: Boolean, val preco: String, val ativo: Boolean )
// Response para lista de atividades (com detalhes completos) data class AtividadesListResponse( val status: Boolean, val status_code: Int, val messagem: String, val atividades: List<AtividadeResponse> )
// Response para atividade única (com detalhes completos) data class AtividadeUnicaResponse( val status: Boolean, val status_code: Int, val messagem: String, val atividade: AtividadeResponse )
// Atividade com detalhes completos e aulas data class AtividadeResponse( val atividade_id: Int, val titulo: String, val descricao: String, val faixa_etaria_min: Int, val faixa_etaria_max: Int, val gratuita: Int, // 0 ou 1 val preco: Double, val ativo: Int, // 0 ou 1 val categoria: String, val instituicao_id: Int, val instituicao_nome: String, val instituicao_foto: String?, val cidade: String, val estado: String, val aulas: List<AulaDetalhe> )
// Detalhes de uma aula dentro da atividade data class AulaDetalhe( val status: String, // "Futura" ou "Encerrada" val aula_id: Int, val hora_fim: String, val data_aula: String, val hora_inicio: String, val vagas_total: Int, val vagas_disponiveis: Int )

package com.example.oportunyfam.model
// Request para criar uma única aula data class AulaRequest( val id_atividade: Int, val data_aula: String, // Formato: "YYYY-MM-DD" val hora_inicio: String, // Formato: "HH:MM:SS" val hora_fim: String, // Formato: "HH:MM:SS" val vagas_total: Int )
// Request para criar várias aulas em lote data class AulaLoteRequest( val id_atividade: Int, val hora_inicio: String, // Formato: "HH:MM:SS" val hora_fim: String, // Formato: "HH:MM:SS" val vagas_total: Int, val datas: List<String> // Lista de datas no formato: "YYYY-MM-DD" )

